#[test]
fn test_encryption_decryption() {
    let n_v = NtruVersion {
        p: 761,
        q: 4591,
        w: 286,
        irr: set_irr(761),
    };
    let keys = key_gen(&n_v);
    let pk = keys.0;
    let sk = keys.1;
    // message
    let m = create_rand_poly(n_v.w,n_v.p);
    // encryption
    let c = encryption(&m, pk, &n_v);
    let result = decryption(c, sk, &n_v);
    for i in 0..result.len(){
        if result[i] != m[i]{
            panic!("NTRU prime failed!");
        }
    }
}
